import React, { useState, useEffect, useContext } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import GameClient from '../services/GameClient';
import { ClientState } from '../types/ClientState';
import { GameClientContext } from '../contexts/GameClientContext';


import {
    NumberButton,
    OperatorButton,
    ControlButton,
    NumbersGrid,
    OperatorGrid,
    ButtonGroup,
    QuitButton,
} from '../components/GameButtons';

import {
    Container,
    Header,
    Title,
    GameArea,
    CurrentPlayerArea,
    OtherPlayersArea,
    PlayerCard,
    PlayerHeader,
    FormulaDisplay,
    ResultDisplay,
    LoginForm,
    Input,
    WinnerDisplay,
    WinnerFormula,
    PlayerName,
    GameControls
} from '../components/GameStyles';

// Define prop types for GamePage
interface GamePageProps {
    mode: 'single' | 'multi';  // Only allow these two values
}


const GamePage: React.FC = () => {
    const navigate = useNavigate();
    const location = useLocation();
    const { client } = useContext(GameClientContext);
    const {  mode } = (location.state as {mode: string});
    console.log("Mode: ", mode);

    const [playerName, setPlayerName] = useState(client?.getPlayerNames() || '');
    const [formula, setFormula] = useState('');
    const [result, setResult] = useState('0');
    const [gameNumbers, setGameNumbers] = useState<string[]>([]); 
    const [winner, setWinner] = useState<string>('');
    const [winnerFormula, setWinnerFormula] = useState<string>('');
    const [status, setStatus] = useState<string>('false');
    const [isJoined, setIsJoined] = useState<boolean>(false);

    const updateDisplay = (client: GameClient) => {
        setFormula(client.getCurrentPlayerFormula());
        setResult(client.getCurrentPlayerResult());
        setGameNumbers(client.getGameNumbersString());
        setStatus(client.getGameStatus());
        if (client.getWinner()) {
            setWinner(client.getWinner());
            setWinnerFormula(client.getWinnerFormula());
        }
    };

/*
    const joinGame = async () => {
        if (!client) {
            console.log("No game client");
            navigate('/');
            return;
        }

        try {
            console.log("Player is joining game: ", playerName);
            const roomID = await client.createRoom(true);
            console.log("Creating Room complete ID: ", roomID);
            await client.joinGame(roomID);
            setIsJoined(true);
        } catch (error) {
          console.error('Error joining game:', error);
          navigate('/');  
        }
      };
*/

    const joinGame = async () => {
        if (!client) {
            console.log("No game client");
            navigate('/');
            return;
        }
        try {
            const roomID = await client.createRoom(true);
            console.log("Creating Room complete ID: ", roomID, playerName);
            setIsJoined(true);
            await client.joinGame(roomID);
            updateDisplay(client);

        } catch (error) {
        console.error('Error joining game:', error);
        navigate('/');  
        }
    };

    useEffect(() => {

        console.log("Initializing game: ", playerName);

        if (!isJoined) {
            joinGame();
        }
    }, [isJoined]);


/*
    useEffect(() => {
        if (!client) {
            navigate('/');
            return;
        }

        updateDisplay(client);

        let interval: NodeJS.Timeout;

        interval = setInterval(async () => {
            await client.fetchGamePage();
            updateDisplay(client);
        }, 1000);
        return () => clearInterval(interval);

    }, [client, navigate]);
*/

    const handleLogout = async () => {
        if (!client) return;
        try {
            await client.leaveGame();
            //resetGameState();
            navigate('/');
        } catch (error) {
            console.error('Error:', error);
        }
    };

    /*
    const resetGameState = () => {
        setPlayerName('');
        setGameClient(null);
        setFormula('');
        setResult('0');
        setGameNumbers([]);
        setIsLoggedIn(false);
    };
    */

    const handleNewGame = async () => {
        if (!client) return;
        try {
            await client.startNewGame();
            setWinner('');
            setWinnerFormula('');
            updateDisplay(client);
        } catch (error) {
            console.error('Error:', error);
        }
    };

    const handleTokenClick = async (token: string) => {
        if (!client) return;
        try {
            await client.addToken(token);
            updateDisplay(client);
        } catch (error) {
            console.error('Error:', error);
        }
    };

    const handleRemove = async () => {
        if (!client) return;
        try {
            await client.removeToken();
            updateDisplay(client);
        } catch (error) {
            console.error('Error:', error);
        }
    };

    const handleClear = async () => {
        if (!client) return;
        try {
            await client.clearFormula();
            updateDisplay(client);
        } catch (error) {
            console.error('Error:', error);
        }
    };

    return (
        <Container>
                <>
                    <Header>
                        <Title>24 Game</Title>
                        <PlayerName>Player: {playerName}</PlayerName>
                        <QuitButton onClick={handleLogout}>Quit Game</QuitButton>
                    </Header>

                    {winner && (
                        <WinnerDisplay>
                            <h2>We have a winner!</h2>
                            <div>Player: {winner}</div>
                            <WinnerFormula>
                                Winning Formula: {winnerFormula}
                            </WinnerFormula>
                        </WinnerDisplay>
                    )}

                    <GameArea>
                        <CurrentPlayerArea>
                            <NumbersGrid>
                                {gameNumbers.map((num, index) => (
                                    <NumberButton 
                                        key={index} 
                                        onClick={() => handleTokenClick(num)}
                                    >
                                        {num}
                                    </NumberButton>
                                ))}
                            </NumbersGrid>

                            <FormulaDisplay>
                                {formula || 'Start building your formula'}
                            </FormulaDisplay>

                            <ResultDisplay>
                                Result: {result}
                            </ResultDisplay>

                            <OperatorGrid>
                                {['+', '-', '*', '/', '(', ')'].map((op) => (
                                    <OperatorButton 
                                        key={op} 
                                        onClick={() => handleTokenClick(op)}
                                    >
                                        {op}
                                    </OperatorButton>
                                ))}
                            </OperatorGrid>

                            <ButtonGroup>
                                <ControlButton onClick={handleClear}>
                                    Clear
                                </ControlButton>
                                <ControlButton onClick={handleRemove}>
                                    Undo
                                </ControlButton>
                                <ControlButton onClick={handleNewGame}>
                                    New Game
                                </ControlButton>
                            </ButtonGroup>
                        </CurrentPlayerArea>

                        <OtherPlayersArea>
                            {client && Object.entries(client['_cells'])
                                .filter(([name]) => name !== playerName)
                                .map(([name, cell]) => (
                                    <PlayerCard key={name}>
                                        <PlayerHeader>{name}</PlayerHeader>
                                        <FormulaDisplay>{cell.formula || 'No formula yet'}</FormulaDisplay>
                                        <ResultDisplay>Result: {cell.value || '0'}</ResultDisplay>
                                    </PlayerCard>
                                ))}
                        </OtherPlayersArea>
                    </GameArea>
                </>

        </Container>
    );
};

export default GamePage;
