import styled from 'styled-components';
import React, { useState, useEffect, useContext } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import GameClient from '../services/GameClient';
import { GameClientContext } from '../contexts/GameClientContext';
import {Rooms, RoomInfo} from '../GameDefinitions';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
`;

const RoomList = styled.div`
  width: 100%;
  max-width: 600px;
  display: flex;
  flex-direction: column;
  gap: 10px;
`;

const RoomCard = styled.div`
  padding: 15px;
  border: 1px solid #d3d6da;
  border-radius: 8px;
  display: flex;
  justify-content: space-between;
  align-items: center;

  &:hover {
    background: #f4f4f4;
  }
`;

const Button = styled.button`
  padding: 10px 20px;
  border: none;
  border-radius: 4px;
  background: #6aaa64;
  color: white;
  cursor: pointer;

  &:hover {
    background: #5c935c;
  }
`;




const RoomSelection: React.FC = () => {
  const navigate = useNavigate();
  const [rooms, setRooms] = useState<RoomInfo[]>([]);
  const { client } = useContext(GameClientContext);

  useEffect(() => {
    let interval: NodeJS.Timeout;

    if (client) {
      interval = setInterval(async () => {
        const rooms = await client.fetchRooms();
        if (rooms.status === 'Empty') {
          setRooms([]);
          return;
        } else {
          console.log("RoomList: ", rooms.roomList);
          setRooms(rooms.roomList);}
      }, 1000);
      return () => clearInterval(interval);
    }
  }, [client]);

  const handleRoomClick = async (roomID: string) => {
    if (!client) return;
    try {
      const response = await client.joinGame(roomID);
      navigate(`/game/multi/${roomID}`);
    } catch (error) {
      console.error('Error joining room:', error);
    }
  };

  const createRoom = async (maxPlayer: string) => {
    if (!client) return;
    try {
      const response = await client.createRoom(maxPlayer);
      navigate('/game/single', {
        state: { 
         mode: 'single' } });

    } catch (error) {
      console.error('Error creating room:', error);
    }
  };


  return (
    <Container>
      <h1>Game Rooms</h1>
      <Button onClick={() => createRoom("4")}>Create New Room</Button>

      <RoomList>
        {rooms.length === 0 ? (
          <p>No rooms available</p>
        ) : ( 
            rooms.map(room => (
            <RoomCard key={room.roomID}>
              <div>
              <h3>Room {room.roomID}</h3>
              <p>Players: {room.hostPlayer}</p>
              <p>Vacancy: {room.vacancySeats}</p>
            </div>
            <Button onClick={() => handleRoomClick(room.roomID)}>
              Join Room
            </Button>
          </RoomCard>
          )))}
      </RoomList>
    </Container>
  );
};

export default RoomSelection;
