import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import GameClient from '../services/GameClient';
import { GameClientContext } from '../contexts/GameClientContext';
import {
    LoginContainer,
    Rule,
    Card,
    Title,
    GameAreaMulti,
    GameTitle,
    LoginForm,
    WelcomeText,
    Label,
    GameRules,
    PlayButton,
    RulesList,
    InputGroup,
    RulesTitle,
    CurrentPlayerAreaMulti,
    OtherPlayersArea,
    PlayerCard,
    PlayerHeader,
    FormulaDisplay,
    ResultDisplay,
    Input,
    SubTitle, 
    WinnerDisplay,
    WinnerFormula,
    PlayerName,
    GameControls
} from '../components/GameStyles';


const LoginPage = () => {
    const { setGameClient } = useContext(GameClientContext);
    const navigate = useNavigate();
    const [playerName, setPlayerName] = useState('');

    const handleLogin = async (e: React.FormEvent) => {
        e.preventDefault();

        if (!playerName.trim()) {
            alert("Player name cannot be empty");
            return 
        }

        const client = new GameClient(playerName);
        try{
            await client.verifyPlayerName();
            setGameClient(client);
            console.log("LoginPage Player: ", client.getPlayerName());

            navigate('/mode-selection');
        } catch (error) {
            if (error instanceof Error) {
                alert(error.message);
            } else {
                console.error('Unexpected error:', error);
                alert("Unexpected error");
            }
        }

    };

    return (
        <LoginContainer>
            <GameTitle>24 Points</GameTitle>
            <SubTitle>Test your math skills!</SubTitle>

            <Card>
                <WelcomeText>Welcome to the Game</WelcomeText>
                <LoginForm onSubmit={handleLogin}>
                    <InputGroup>
                        <Label>Enter Your Name</Label>
                        <Input
                            value={playerName}
                            onChange={(e) => setPlayerName(e.target.value)}
                            placeholder="Your game name"
                            required
                        />
                    </InputGroup>
                    <PlayButton type="submit">
                        Start Playing!
                    </PlayButton>
                </LoginForm>

                <GameRules>
                    <RulesTitle>How to Play:</RulesTitle>
                    <RulesList>
                        <Rule>• Use all four numbers exactly once</Rule>
                        <Rule>• Use basic operations (+, -, ×, ÷)</Rule>
                        <Rule>• Make the result equal to 24</Rule>
                    </RulesList>
                </GameRules>
            </Card>
        </LoginContainer>
    );
};

export default LoginPage;
